import { useLocation } from 'react-router-dom'
import { useState, useContext } from 'react'
import { SearchContext } from '../context/SearchContextProvider'
import { Outlet } from 'react-router-dom'
import styled from 'styled-components'
import SearchBar from './SearchBar'
import Sidebar from './Sidebar'
import Header from './Header'

const Layout = () => {
  const { pathname } = useLocation()
  const [selectedFilter, setSelectedFilter] = useState(searchType)
  const { setSearchType, searchType } = useContext(SearchContext)

  const handleFilterChange = (filter) => {
    setSearchType(filter)
    setSelectedFilter(filter)
  }

  return (
    <>
      <Header />
      <Container>
        <Sidebar />
        <Main>
          <FilterContainer
            style={{
              display: pathname === '/userList' ? 'none' : 'flex',
            }}
          >
            <FilterHeader>Filter by:</FilterHeader>
            <FilterButton
              isActive={selectedFilter === 'popularity'}
              onClick={() => handleFilterChange('popularity')}
            >
              Popularity
            </FilterButton>
            <FilterButton
              isActive={selectedFilter === 'votes'}
              onClick={() => handleFilterChange('votes')}
            >
              Votes
            </FilterButton>
            <FilterButton
              isActive={selectedFilter === 'date'}
              onClick={() => handleFilterChange('date')}
            >
              Date
            </FilterButton>
            <SearchBar />
          </FilterContainer>

          <Outlet />
        </Main>
      </Container>
    </>
  )
}

const Container = styled.div`
  display: grid;
  min-height: 100%;
  grid-template-columns: 1fr 5fr;
`

const Main = styled.main`
  background: linear-gradient(to top, #0d253f, #01b4e4, #90cea1);
  min-height: 100%;
`

const FilterContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 1em;
`

const FilterHeader = styled.h2`
  color: #fff;
  text-decoration: underline;
  text-decoration-color: #01b4e4;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.2);
  font-size: 1.5em;
  margin-right: 1em;
`

const FilterButton = styled.button`
  background-color: ${(props) => (props.isActive ? '#01b4e4' : '#fff')};
  border: none;
  border-radius: 0.25em;
  color: ${(props) => (props.isActive ? '#fff' : '#0d253f')};
  cursor: pointer;
  font-size: 1em;
  margin-right: 0.5em;
  padding: 0.5em 1em;

  &:hover {
    scale: ${(props) => (props.isActive ? '1' : '1.05')};
    transition: all 0.2s ease-in-out;
  }
`

export default Layout
