import styled from 'styled-components'
import { SearchContext } from '../context/SearchContextProvider'
import { useContext, useState } from 'react'

function Filters() {
  const [selectedFilter, setSelectedFilter] = useState('discover')

  const { setSearchType, setStartDate, setEndDate } = useContext(SearchContext)

  const handleFilterChange = (filter) => {
    setSearchType(filter)
    setSelectedFilter(filter)
    selectedFilter === filter && setSelectedFilter('discover')
  }

  return (
    <>
      <FilterButton
        isActive={selectedFilter === 'popularity'}
        onClick={() => handleFilterChange('popularity')}
      >
        Popularity
      </FilterButton>
      <FilterButton
        isActive={selectedFilter === 'votes'}
        onClick={() => handleFilterChange('votes')}
      >
        Votes
      </FilterButton>
      <FilterButton
        isActive={selectedFilter === 'date'}
        onClick={() => handleFilterChange('date')}
      >
        Date
      </FilterButton>
      {selectedFilter === 'date' && (
        <>
          <FilterInput
            type="date"
            onChange={(e) => setStartDate(e.target.value)}
          />
          <FilterInput
            type="date"
            onChange={(e) => setEndDate(e.target.value)}
          />
        </>
      )}
    </>
  )
}

const FilterButton = styled.button`
  background-color: ${(props) => (props.isActive ? '#01b4e4' : '#fff')};
  border: none;
  border-radius: 0.25em;
  color: ${(props) => (props.isActive ? '#fff' : '#0d253f')};
  cursor: pointer;
  font-size: 1em;
  margin-right: 0.5em;
  padding: 0.5em 1em;

  &:hover {
    scale: ${(props) => (props.isActive ? '1' : '1.05')};
    transition: all 0.2s ease-in-out;
  }
`

const FilterInput = styled.input`
  border: none;
  border-radius: 0.25em;
  font-size: 1em;
  margin-right: 0.5em;
  padding: 0.5em 1em;
`

export default Filters
